import React, { useState, useEffect } from "react";

const AllCategories = (props) => {
  const [categoryData, setCategoryData] = useState([]);
  const fetchData = async () => {
    const url = "http://localhost:4000/electronics";
    const response = await fetch(url);
    const data = await response.json();
    setCategoryData(data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  //   filter Category Data
  const uniqueCategory = categoryData.filter((data, index, array) => {
    const firstIndex = array.findIndex((item) => item.type === data.type);
    return index === firstIndex;
  });

  //   map through categoris
  const categoris = uniqueCategory.map((category, index) => {
    return (
      <div key={index}>
        <p>{category.type}</p>
      </div>
    );
  });
  props.onSaveAllCategoris(uniqueCategory);
  return (
    <div>
      <div>
        <h2>{categoris}</h2>
      </div>
    </div>
  );
};

export default AllCategories;
