import React, { useRef } from "react";
import classes from "./DailyHighlights.module.css";
import AllDailyHighlights from "./AllDailyHighlights";

const DailyHighlights = () => {
  const arrowRef = useRef(null);

  const rightScrollHnalder = () => {
    if (arrowRef.current) {
      arrowRef.current.scrollLeft += 350;
      console.log("right");
    }
  };

  const leftScrollHnalder = () => {
    if (arrowRef.current) {
      arrowRef.current.scrollLeft -= 350;
      console.log("left");
    }
  };
  return (
    <div className={classes.daily}>
      <h1>Daily Highlight</h1>

      <AllDailyHighlights />
    </div>
  );
};

export default DailyHighlights;
