import React, { useState, useEffect } from "react";
import FreeShippingBar from "../Components/FreeShippingBar/FreeShippingBar";
import NewsBar from "../Components/NewsBar/NewsBar";
import GoogleNews from "../assests/Pixel_8_Pro.jpg";
import HuaewiNews from "../assests/desk.webp";
import LGNews from "../assests/MMSA_TSR_mob_new.jpg";
import MetaNews from "../assests/MMSA_TSR_mob_new (1).jpg";
import classes from "./Home.module.css";

const HomePage = () => {
  const [pressed, setPressed] = useState(false);
  const [startX, setStartX] = useState(0);
  useEffect(() => {
    const wrapperElement = document.querySelector(".wrapper");
    if (wrapperElement) {
      const handleMouseDown = (e) => {
        setPressed(true);
        setStartX(e.clientX);
        wrapperElement.style.cursor = "grabbing";
      };

      const handleMouseLeave = () => {
        setPressed(false);
      };

      const handleMouseUp = () => {
        setPressed(false);
        wrapperElement.style.cursor = "grab";
      };

      const handleMouseMove = (e) => {
        if (!pressed) {
          return;
        }
        wrapperElement.scrollLeft += startX - e.clientX;
      };

      wrapperElement.addEventListener("mousedown", handleMouseDown);
      wrapperElement.addEventListener("mouseleave", handleMouseLeave);
      window.addEventListener("mouseup", handleMouseUp);
      wrapperElement.addEventListener("mousemove", handleMouseMove);

      return () => {
        wrapperElement.removeEventListener("mousedown", handleMouseDown);
        wrapperElement.removeEventListener("mouseleave", handleMouseLeave);
        window.removeEventListener("mouseup", handleMouseUp);
        wrapperElement.removeEventListener("mousemove", handleMouseMove);
      };
    }
  }, [pressed, startX]);
  return (
    <>
      <FreeShippingBar />
      <div
        className={classes.wrapper}
        onMouseDown={handleMouseDown}
        onMouseLeave={handleMouseLeave}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
        style={{ overflowX: "auto", whiteSpace: "nowrap" }}
      >
        <NewsBar
          image={GoogleNews}
          newsTitle="novelty"
          newsHeading="Google News"
          newsPara="Get the new Google Pixel 8 series and Pixel Watch 2 with attractive extras and services now at Electra Hub"
        />
        <NewsBar
          image={HuaewiNews}
          newsTitle="novelty"
          newsHeading="More Than Just The Time"
          newsPara="Buy the new HUAWEI Watch GT 4 now and get HUAWEI FreeBuds SE 2 - only until October 15th."
        />
        <NewsBar
          image={LGNews}
          newsTitle="Only Until Novemebr 5TH"
          newsHeading="A Big Movie Theater"
          newsPara="Golden times for LG fans: Get up to â‚¬ 5,300 cashback on promotional products for Smart TVs &amp; Audio"
        />
        <NewsBar
          image={MetaNews}
          newsTitle="Pre-order now & secure it before anyone else"
          newsHeading="Totally Digital Real"
          newsPara="Meta Quest 3: groundbreaking mixed reality + powerful technology for an immersive gaming experience"
        />
      </div>
    </>
  );
};

export default HomePage;
